let url 		= window.location.href,
	productId 	= url.match(/id=([0-9]+)/),
	referrer 	= url.match(/ref=([a-z0-9-.]+)/),
	domain 		= url.match('domain=([a-z0-9-.]+)'),
	user_agent 	= navigator.userAgent,
	filter_bot 	= '/google|bot|bing|yahoo|pinterest|yandex|facebook|webmaster|spider|crawlr/i',
	affiliateId = '_dVDcCBS'; // updated 29-08-20
if(!user_agent.match(filter_bot)){
	redirect_link = 'https://s.click.aliexpress.com/deep_link.htm?dl_target_url=https%3A%2F%2Fwww.aliexpress.com%2Fitem%2F-%2F'+productId[1]+'.html&aff_short_key='+affiliateId+'&dp='+domain[1]+'&af='+referrer[1]+'&cn=firebot';
	window.location.replace(redirect_link);
}
